const express = require('express');
var cors = require('cors');
// const antlr4 = require('antlr4');
// const { CommonTokenStream, InputStream } = antlr4;

// const ScenarioLexer = import('./Antlr/scenarioLexer.js');
// const ScenarioParser = import('./Antlr/scenarioParser.js');
// const HtmlScenarioListener = import('./Antlr/HtmlScenarioListener.js');

// class CustomErrorListener extends antlr4.ErrorListener{
//     syntaxError(recognizer, offendingSymbol, line, column, msg, e) {
//         throw new Error(`Syntax error at line ${line}:${column} - ${msg}`);
//     }
// }

const corsOptions ={
   origin:'*', 
   credentials:true,            //access-control-allow-credentials:true
   optionSuccessStatus:200,
}


var HOST_NAME = 'localhost';
var PORT_EXT = 8080;
var app = express();
var router = express.Router();
var bodyParser = require("body-parser");
const fs = require('fs');
const ScenarioModel = require('./ScenarioModel.js');
const scenariosFolders = '../ApiRestNaVisu4D/ApiRestNaVisu4D/data/scenarios';
app.get('/', (req, res) => {
    res.send('hi!');
  });

// add it to allow files and not get a payload to long 413 error
app.use(bodyParser.json({limit: '50mb'}));
app.use(bodyParser.urlencoded({limit: '50mb', extended: true}));
app.use(cors())
app.options('*', cors());
app.use(router);


app.listen(PORT_EXT, HOST_NAME, function () {
    console.log("Server listen  http://" + HOST_NAME + ":" + PORT_EXT);
});


router.route('/scenarios')
    .get(async function (req, res) {
        // need change here
        const scenariosData = fs.readdirSync(scenariosFolders).map(folder => {
            // maybe a a filter is better
            const scenarioFiles = fs.readdirSync(scenariosFolders + '/' + folder).reduce((acumulator, subContent) => {
                if (subContent.includes('.json')) {
                    const data = fs.readFileSync(scenariosFolders + '/' + folder + '/' + subContent)
                    const scenarioObj = new ScenarioModel(JSON.parse(data));
                    scenarioObj.formatForRes();
                    scenarioObj.fileName = folder;
                    acumulator = {...acumulator, ...scenarioObj};
                }
                return acumulator;
            }, {});
            return scenarioFiles;
        });
        res.json(scenariosData);

    })
    
    .post((req, res) => {
        const scenario = new ScenarioModel(req.body)
        scenario.save(req.body.fileName);
        // res.writeHead(200, {
        //     'Content-Type': 'text/html',
        // });
        // res.write('<html><head><meta charset="UTF-8"/></head><body>');
        // try{
        // var input = "blablabla*bbox,48.0,-5.0,49.0,2.0*move,flyTo,ship,-4.54,48.57,900000.0*layer,oceanography,currents,tidalAtlas,2D,max*navigation,pilotchart,NAC,3";
        // var chars = new InputStream(input, true)
        // var lexer = new ScenarioLexer(chars);
        // var tokens = new CommonTokenStream(lexer);
        // var parser = new ScenarioParser(tokens);
        // parser.removeErrorListeners(); // Retire les listeners d'erreur par défaut
        // parser.addErrorListener(new CustomErrorListener()); // Ajoute votre CustomErrorListener
        
        // parser.buildParseTrees = true;
        // var tree = parser.scenario();
        // var htmlScenario = new HtmlScenarioListener(res);
        // antlr4.tree.ParseTreeWalker.DEFAULT.walk(htmlScenario, tree);
        // res.write('</body></html>');
        // res.end();
        // }catch (error) {
        //     console.error(error.message);
        //     res.write(`Error: ${error.message}`);
        //     res.end();
        //     return res; // Arrête l'exécution supplémentaire dans cette requête
        // }
        
        // return res.json({
        //     data: req.body,
        //     methode: req.method
        // });
        return loadMyModule(res);
    })
    .delete(function (req, res) {
        let scenarioName = req.body.title;
        let targetDir = scenariosFolders + scenarioName;
        if (fs.existsSync(targetDir)) {
            fs.rmSync(targetDir, {recursive: true, force: true});
            return res.json('Scenario deleted');
        } else {
            return res.json('Scenario not found');
        }
    })
    .put(function (req, res) {
    });

router.route('/scenarioFilesPath')
    .post((req, res) => {
        const main_directory_name = scenariosFolders + '/' + req.body.fileName;
        if (!fs.existsSync(main_directory_name)) {
            return res.json('scenario don"t exist');
        }
        if (!fs.existsSync(main_directory_name + '/' + req.body.fileName + '.pdf')) {
            return res.json('scenario have no exports files');
        }
        let files_paths = {
            'pdf': {'name': 'export', 'path': main_directory_name + '/' + req.body.fileName + '.pdf'},
